mapper.update:
UPDATE kakeibo SET
	settlement_date = #{settlementDate}, expense_item_id = #{expenseItemId}, expenditure_amount = #{expenditureAmount}, income_amount = #{incomeAmount}, settlement_id = #{settlementId}, used_store = #{usedStore}, remarks = #{remarks}
WHERE
	id = #{id}

UPDATE kakeibo SET 
	is_deleted = #{isDeleted}
WHERE 
	id = #{id}
